import React, { Component } from "react";
import {
  VictoryPie,
  VictoryChart,
  VictoryLine,
  VictoryTheme,
  VictoryBar
} from "victory";

import Table from "../../../components/Table";
import data from "../../../data/data.json";
import { url } from "../../../config";

/**
|--------------------------------------------------
| renders the Dashboard page
|--------------------------------------------------
*/
export default class index extends Component {
  constructor(props) {
    super(props);

    this.state = {
      business: [],
      businesData: [],
      stats: [],
      users: []
    };
  }

  componentDidMount() {
    this.fetchAllBusiness();
    this.fetchAllUsers();
  }

  fetchAllBusiness = () => {
    fetch(`${url}/api/business`, {
      method: "GET",
      mode: "cors",
      headers: {
        "Access-Control-Allow-Origin": "*",
        "Content-Type": "application/json; charset=utf-8",
        Accept: "application/json"
      }
    })
      .then(res => res.json())
      .then(data => {
        if (data.success) {
          let business = [];
          let stats = [];

          data.businesses.map(x => {
            let statistics = {
              x: x.type_of_business,
              y: x.company_name
            };
            stats.push(statistics);
            this.setState({ stats });
          });

          this.setState({ business, businessData: data.businesses });
          // console.log(this.state.business);
        }
      })
      .catch(err => {
        console.log("Error for business page", err);

        alert(
          "Error connecting to server",

          [{ text: "OK", onClick: () => null }],
          { cancelable: false }
        );
      });
  };

  fetchAllUsers = () => {
    fetch(`${url}/api/users`, {
      method: "GET",
      mode: "cors",
      headers: {
        "Access-Control-Allow-Origin": "*",
        "Content-Type": "application/json; charset=utf-8",
        Accept: "application/json"
      }
    })
      .then(res => res.json())
      .then(data => {
        if (data.success) {
          let users = [];
          data.users.map(x => {
            let user = {
              x: x.user_type,
              y: data.users.length
            };
            users.push(user);
          });

          this.setState({ users });
        }
      })
      .catch(err => {
        console.log("Error for users page", err);

        alert(
          "Error connecting to server",

          [{ text: "OK", onClick: () => null }],
          { cancelable: false }
        );
      });
  };

  render() {
    return (
      <div className="main-dashboard-container">
        <h3 className="page-title">Dashboard</h3>
        <div className="container-fluid">
          <div className="graph">
            <div className="graph-container">
              <p>Business Types</p>
              <VictoryChart
                theme={VictoryTheme.material}
                width={700}
                height={300}
              >
                <VictoryBar
                  horizontal
                  cornerRadius={10}
                  data={this.state.stats}
                  style={{
                    label: {
                      fontSize: 16
                    },
                    data: {
                      fill: ({ y }) =>
                        y > 18 ? "rgb(95,153,252)" : "rgb(12,96,250)"
                    }
                  }}
                  animate={{
                    duration: 2000,
                    onLoad: { duration: 2000 }
                  }}
                />
              </VictoryChart>
            </div>
            <div className="graph-container">
              <p>User Vs User Agents</p>
              <VictoryChart
                theme={VictoryTheme.material}
                width={700}
                height={300}
              >
                <VictoryBar
                  cornerRadius={10}
                  data={this.state.users}
                  style={{
                    label: {
                      fontSize: 16
                    },
                    data: {
                      fill: ({ y }) =>
                        y > 18 ? "rgb(95,153,252)" : "rgb(12,96,250)"
                    }
                  }}
                  animate={{
                    duration: 2000,
                    onLoad: { duration: 2000 }
                  }}
                />
              </VictoryChart>
            </div>
            {/* <div className="graph-container">
              <p>Graph</p>
              <VictoryChart theme={VictoryTheme.material}>
                <VictoryLine
                  style={{
                    data: { stroke: "rgb(12,96,250)" },
                    parent: { border: "1px solid #ccc" }
                  }}
                  data={[
                    { x: 1, y: 2 },
                    { x: 2, y: 3 },
                    { x: 3, y: 5 },
                    { x: 4, y: 4 },
                    { x: 5, y: 7 }
                  ]}
                  animate={{
                    duration: 2000,
                    onLoad: { duration: 2000 }
                  }}
                />
              </VictoryChart>
            </div> */}
            {/* <div className="graph-container">
              <p>Graph</p>
              <VictoryPie
                innerRadius={200}
                data={[{ x: 11, y: 35 }, { x: 11, y: 40 }]}
                style={{
                  data: {
                    fill: ({ y }) =>
                      y > 39 ? "rgb(95,153,252)" : "rgb(12,96,250)"
                  }
                }}
                animate={{
                  duration: 2000,
                  onLoad: { duration: 2000 }
                }}
              />
            </div> */}
          </div>
          <div className="graph">
            <div className="graph-container-2">
              <p>Most Recent Feedback </p>
              <table class="table table-bordered table-striped">
                <thead>
                  <tr>
                    <th scope="col">#</th>
                    <th scope="col">First</th>
                    <th scope="col">Last</th>
                    <th scope="col">Handle</th>
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <th scope="row">1</th>
                    <td>Mark</td>
                    <td>Otto</td>
                    <td>@mdo</td>
                  </tr>
                  <tr>
                    <th scope="row">2</th>
                    <td>Jacob</td>
                    <td>Thornton</td>
                    <td>@fat</td>
                  </tr>
                  <tr>
                    <th scope="row">3</th>
                    <td>Larry</td>
                    <td>the Bird</td>
                    <td>@twitter</td>
                  </tr>
                </tbody>
              </table>
            </div>
            <div className="graph-container-2">
              <p>Pending Agent Request</p>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
