import React, { Component } from "react";
import Table from "../../../components/Table";
import data from "../../../data/data.json";
import { url } from "../../../config";

export default class index extends Component {
  constructor(props) {
    super(props);

    this.state = {
      name: "",
      desc: "",
      amount: "",
      quantity: "",
      expiry: "",
      addVoucher: false,
      vouchers: [],
      rowSelected: false
    };
  }

  /**
  |--------------------------------------------------
  | on page load run the getVouchers function
  |--------------------------------------------------
  */
  componentDidMount() {
    this.getVouchers();
  }

  /**
  |--------------------------------------------------
  | gets the state of the table row that has been clicked
  |--------------------------------------------------
  */
  passedFromChild = (i, state) => {
    console.log(i);
    this.setState({ rowSelected: state });
  };

  /**
  |--------------------------------------------------
  | add a voucher
  |--------------------------------------------------
  */
  addVoucher = () => {
    this.setState({ addVoucher: false });

    fetch(`${url}/api/vouchers`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json; charset=utf-8",
        Accept: "application/json"
      },
      body: JSON.stringify({
        name: this.state.name,
        description: this.state.desc,
        amount: parseInt(this.state.amount),
        quantity: parseInt(this.state.quantity),
        expiry: this.state.expiry
      })
    })
      .then(res => res.json())

      .then(data => {
        if (data.success === true) {
          alert(data.message);
          window.location.reload();
        }
      })

      .catch(err => console.log(err));
  };

  /**
  |--------------------------------------------------
  | get all voucher info and display it
  |--------------------------------------------------
  */
  getVouchers = () => {
    fetch(`${url}/api/vouchers`, {
      method: "GET",
      mode: "cors",
      headers: {
        "Access-Control-Allow-Origin": "*",
        "Content-Type": "application/json; charset=utf-8",
        Accept: "application/json"
      }
    })
      .then(res => res.json())
      .then(data => {
        if (data.success) {
          let vouchers = [];
          data.vouchers.map(x => {
            let user = {
              name: x.name,
              description: x.description,
              amount: x.amount,
              quantity: x.quantity,
              expiry: x.expiry
            };
            vouchers.push(user);
            return vouchers;
          });

          this.setState({ vouchers });
        }
      })
      .catch(err => {
        console.log("Error for users page", err);

        alert(
          "Error connecting to server",

          [{ text: "OK", onClick: () => null }],
          { cancelable: false }
        );
      });
  };

  /**
  |--------------------------------------------------
  | navigates to view voucher page
  |--------------------------------------------------
  */
  viewVoucher = () => {
    this.props.history.push("/viewvoucher");
  };

  render() {
    return (
      <div className="dashboard-container">
        {this.state.addVoucher === true ? (
          <div className="cat-modal-opacity" />
        ) : null}
        <h3 className="page-title voucher-main-title">Vouchers</h3>
        <button
          type="button"
          className="btn btn-primary add-voucher-btn"
          onClick={() => this.setState({ addVoucher: true })}
        >
          <img
            src="https://png.pngtree.com/svg/20161118/6fc980719c.svg"
            alt="add-modal-icon"
          />
        </button>

        {this.state.addVoucher === true ? (
          <div className="voucher-popup-container">
            <div>
              <h3 className="cat-pop-title">Category Title</h3>
              <button
                className="categoryXBtn"
                onClick={() => this.setState({ addVoucher: false })}
              >
                <img
                  alt="img"
                  src="https://image.flaticon.com/icons/png/512/458/458595.png"
                />
              </button>
            </div>
            <div>
              <input
                className="form-control voucher-pop-input"
                value={this.state.name}
                placeholder="Name"
                onChange={e => this.setState({ name: e.target.value })}
              />
              <input
                className="form-control voucher-pop-input"
                value={this.state.desc}
                placeholder="Description"
                onChange={e => this.setState({ desc: e.target.value })}
              />
              <input
                className="form-control voucher-pop-input"
                value={this.state.amount}
                placeholder="Amount"
                type="number"
                onChange={e => this.setState({ amount: e.target.value })}
              />
              <input
                className="form-control voucher-pop-input"
                value={this.state.quantity}
                placeholder="Quantity"
                type="number"
                onChange={e => this.setState({ quantity: e.target.value })}
              />
              <input
                className="form-control voucher-pop-input"
                value={this.state.expiry}
                type="date"
                placeholder="Expiry"
                onChange={e => this.setState({ expiry: e.target.value })}
              />
              <button
                className="btn cat-save-btn"
                onClick={() => this.addVoucher()}
              >
                Save
              </button>
            </div>
          </div>
        ) : null}
        <div className="container-fluid" style={{ paddingTop: "7.5rem" }}>
          <Table
            head={data.tHeadVoucherTable}
            body={this.state.vouchers}
            method={this.passedFromChild}
          />
        </div>
        {this.state.rowSelected === true ? (
          <div>
            {this.state.approved.map((x, i) => (
              <div className="view-business-container" key={i}>
                <button
                  className="exit-btn-view-business"
                  onClick={() => this.setState({ rowSelected: false })}
                >
                  <img
                    alt="selected-user-x-icon"
                    src="https://img.pngio.com/index-of-v2-imgs-x-png-black-and-white-256_256.png"
                  />
                </button>
                <div className="business-profile-container">
                  <img
                    alt="selected-user-profile-icon"
                    src="https://www.biography.com/.image/ar_1:1%2Cc_fill%2Ccs_srgb%2Cg_face%2Cq_auto:good%2Cw_300/MTQ3NjM5MzQyOTU5OTYxNDc2/lil_wayne_photo_by_ray_tamarra_getty_images_entertainment_getty_56680625.jpg"
                  />
                  <span>
                    {x.f_name} {x.l_name}
                  </span>
                </div>
                <div className="business-email-container">
                  <span>Email: </span>
                  <span className="email-span">{x.email} </span>
                </div>
                <div className="business-email-container">
                  <span>Identification: </span>
                  <span className="email-span">{x.identification} </span>
                </div>
                <div className="business-email-container">
                  <span>Contact: </span>
                  <span className="email-span">{x.contact} </span>
                </div>
                <div className="business-email-container">
                  <span>Legal Name: </span>
                  <span className="email-span">{x.legal_name} </span>
                </div>
                <div className="border-view-business" />
                <div className="view-business-tabs">
                  {this.state.businessSelected === false ? (
                    <button
                      onClick={() =>
                        this.setState({
                          businessSelected: true,
                          billingSelected: false,
                          openingTimes: false,
                          storeSelected: false,
                          featuredItems: false
                        })
                      }
                    >
                      Business
                    </button>
                  ) : (
                    <button
                      className="view-business-tab-active"
                      onClick={() =>
                        this.setState({
                          businessSelected: true,
                          billingSelected: false,
                          openingTimes: false,
                          storeSelected: false,
                          featuredItems: false
                        })
                      }
                    >
                      Business
                    </button>
                  )}
                </div>

                {this.state.businessSelected === true ? (
                  <div className="main-field-container">
                    <ul>
                      <li>hello</li>
                    </ul>
                  </div>
                ) : null}
              </div>
            ))}
          </div>
        ) : null}
      </div>
    );
  }
}
