import React, { Component } from "react";
import Table from "../../../components/Table";
import data from "../../../data/data.json";
import { url } from "../../../config";

export default class index extends Component {
  constructor(props) {
    super(props);

    this.state = {
      pendingTab: true,
      approvedTab: false,
      declineTab: false,
      approveDecline: false,
      approved: [],
      pending: [],
      decline: [],
      business: [],
      ids: []
    };
  }

  /**
  |--------------------------------------------------
  | on load get all business request data
  |--------------------------------------------------
  */
  componentDidMount() {
    this.fetchBusiness();
  }

  /**
  |--------------------------------------------------
  | fetches all business request data from api
  |--------------------------------------------------
  */
  fetchBusiness = async () => {
    fetch(`${url}/api/category`, {
      method: "GET",
      mode: "cors",
      headers: {
        "Access-Control-Allow-Origin": "*",
        "Content-Type": "application/json; charset=utf-8",
        Accept: "application/json"
      }
    })
      .then(res => res.json())
      .then(data => {
        if (data.success) {
          let business = data.businesses;
          let approved = [];
          let decline = [];
          let pending = [];

          business.map(x =>
            x.isPending
              ? pending.push(x)
              : x.isApproved
              ? approved.push(x)
              : x.isDeclined
              ? decline.push(x)
              : console.log("No Status")
          );

          let pendingReceived = [];

          pending.map(x => {
            let pend = {
              user_id: x.user_id,
              isPending: "is Pending",
              dateCreated: x.dateCreated
            };
            this.state.ids.push(x._id);

            pendingReceived.push(pend);
          });

          this.setState({ pending: pendingReceived });

          let approveReceived = [];
          approved.map(x => {
            let approve = {
              user_id: x.user_id,
              isPending: "is Approved",
              dateCreated: x.dateCreated
            };
            approveReceived.push(approve);
          });

          this.setState({ approved: approveReceived });

          let declineReceived = [];
          decline.map(x => {
            let decline = {
              user_id: x.user_id,
              isPending: "is Declined",
              dateCreated: x.dateCreated
            };
            declineReceived.push(decline);
          });

          this.setState({ decline: declineReceived });
        }
      })
      .catch(err => {
        console.log("Error for business page", err);

        alert(
          "Error connecting to server",

          [{ text: "OK", onClick: () => null }],
          { cancelable: false }
        );
      });
  };

  filterBusiness = () => {};

  /**
  |--------------------------------------------------
  | receives and sets state 
    selected row data from the Table component
  |--------------------------------------------------
  */
  passedFromChild = (i, state) => {
    console.log(state);
  };

  render() {
    return (
      <div className="dashboard-container">
        {this.state.approveDecline === true ? (
          <div className="modal-opacity" />
        ) : null}
        <h3
          className="page-title business-request-title"
          style={{ marginLeft: "1rem" }}
        >
          Agent Requests
        </h3>
        <div className="business-request-tabs-container">
          <button
            onClick={() =>
              this.setState({
                pendingTab: true,
                approvedTab: false,
                declineTab: false
              })
            }
          >
            Pending
          </button>
          <button
            onClick={() =>
              this.setState({
                pendingTab: false,
                approvedTab: true,
                declineTab: false
              })
            }
          >
            Approved
          </button>
          <button
            onClick={() =>
              this.setState({
                pendingTab: false,
                approvedTab: false,
                declineTab: true
              })
            }
          >
            Decline
          </button>
        </div>
        <div
          className="container-fluid bR-mobile-container"
          style={{ paddingTop: "5.2rem" }}
        >
          {this.state.pendingTab === true ? (
            <Table
              head={data.tHeadARequestPendingTable}
              body={this.state.pending}
              button={data.tBodyButton}
              method={this.passedFromChild}
              id={this.state.ids}
              model={"business"}
            />
          ) : null}
          {this.state.approvedTab === true ? (
            <Table
              head={data.tHeadARequestTable}
              body={this.state.approved}
              method={this.passedFromChild}
            />
          ) : null}
          {this.state.declineTab === true ? (
            <Table
              head={data.tHeadARequestTable}
              body={this.state.decline}
              method={this.passedFromChild}
            />
          ) : null}
          {this.state.approveDecline === true ? (
            <div className="approve-decline-container">
              <button>Approve</button>
              <button onClick={() => this.setState({ approveDecline: false })}>
                Decline
              </button>
            </div>
          ) : null}
        </div>
      </div>
    );
  }
}
