import React, { Component } from "react";
import Table from "../../../components/Table";
import data from "../../../data/data.json";
import { url } from "../../../config";

export class index extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isChecked: false,
      filterUsers: "all",
      merchants: [],
      sendState: false,
      sendData: null,
      rowSelected: false,
      businessSelected: false,
      billingSelected: false,
      openingTimes: false,
      storeSelected: false,
      featuredItems: false,
      approvedSelected: []
    };
  }

  /**
  |--------------------------------------------------
  | receives and sets state 
    selected row data from the Table component
  |--------------------------------------------------
  */
  passedFromChild = (i, state) => {
    // let arr = this.state.sendData;
    // let send = this.state.sendState;
    // this.setState({ sendData: arr });
    // this.setState({ sendState: send });
    // console.log(this.state.sendData);
    // console.log(this.state.sendState);
  };

  /**
  |--------------------------------------------------
  | On page load, fetch from api
  |--------------------------------------------------
  */
  componentDidMount() {
    this.fetchAllMerchants();
  }

  /**
  |--------------------------------------------------
  | Fetches data from Api and stores in an array
  |--------------------------------------------------
  */
  fetchAllMerchants = () => {
    fetch(`${url}/api/merchants`, {
      method: "GET",
      mode: "cors",
      headers: {
        "Access-Control-Allow-Origin": "*",
        "Content-Type": "application/json; charset=utf-8",
        Accept: "application/json"
      }
    })
      .then(res => res.json())
      .then(data => {
        if (data.success) {
          let merchants = [];
          data.merchants.map(x => {
            this.setState({ approvedSelected: x });
            let merchant = {
              f_name: x.f_name,
              l_name: x.l_name,
              email: x.email,
              contact: x.contact,
              dateCreated: x.dateCreated
            };
            merchants.push(merchant);
          });

          this.setState({ merchants });
        }
      })
      .catch(err => {
        console.log("Error for merchants page", err);

        alert(
          "Error connecting to server",

          [{ text: "OK", onClick: () => null }],
          { cancelable: false }
        );
      });
  };

  /**
  |--------------------------------------------------
  | changes state on toggle
  |--------------------------------------------------
  */
  toggle = async () => {
    await this.setState({ isChecked: !this.state.isChecked });
    console.log(this.state.isChecked);
    if (!this.state.isChecked) {
      console.log("Users Page");
    } else if (this.state.isChecked) {
      setTimeout(() => {
        this.props.history.push("/useragent");
      }, 400);
    }
  };

  render() {
    return (
      <div className="dashboard-container">
        {this.state.rowSelected === true ? (
          <div className="modal-opacity" />
        ) : null}
        <div className="dashboard-header">
          <h3 className="table-page-title">Merchants</h3>
        </div>

        <div className="container-fluid" style={{ paddingTop: "13rem" }}>
          <Table
            head={data.tHeadBusiness}
            body={this.state.merchants}
            method={this.passedFromChild}
          />
        </div>

        {this.state.rowSelected === true ? (
          <div>
            {this.state.approvedSelected.map((x, i) => (
              <div className="view-business-container" key={i}>
                <button
                  className="exit-btn-view-business"
                  onClick={() => this.setState({ rowSelected: false })}
                >
                  <img
                    alt="selected-user-x-icon"
                    src="https://img.pngio.com/index-of-v2-imgs-x-png-black-and-white-256_256.png"
                  />
                </button>
                <div className="business-profile-container">
                  <img
                    alt="selected-user-profile-icon"
                    src="https://www.biography.com/.image/ar_1:1%2Cc_fill%2Ccs_srgb%2Cg_face%2Cq_auto:good%2Cw_300/MTQ3NjM5MzQyOTU5OTYxNDc2/lil_wayne_photo_by_ray_tamarra_getty_images_entertainment_getty_56680625.jpg"
                  />
                  <span>
                    {x.f_name} {x.l_name}
                  </span>
                </div>
                <div className="business-email-container">
                  <span>Email: </span>
                  <span className="email-span">{x.email} </span>
                </div>
                <div className="business-email-container">
                  <span>Identification: </span>
                  <span className="email-span">{x.identification} </span>
                </div>
                <div className="business-email-container">
                  <span>Contact: </span>
                  <span className="email-span">{x.contact} </span>
                </div>
                <div className="business-email-container">
                  <span>Legal Name: </span>
                  <span className="email-span">{x.legal_name} </span>
                </div>
                <div className="border-view-business" />
                <div className="view-business-tabs">
                  {this.state.businessSelected === false ? (
                    <button
                      onClick={() =>
                        this.setState({
                          businessSelected: true,
                          billingSelected: false,
                          openingTimes: false,
                          storeSelected: false,
                          featuredItems: false
                        })
                      }
                    >
                      Business
                    </button>
                  ) : (
                    <button
                      className="view-business-tab-active"
                      onClick={() =>
                        this.setState({
                          businessSelected: true,
                          billingSelected: false,
                          openingTimes: false,
                          storeSelected: false,
                          featuredItems: false
                        })
                      }
                    >
                      Business
                    </button>
                  )}

                  {this.state.billingSelected === false ? (
                    <button
                      onClick={() =>
                        this.setState({
                          businessSelected: false,
                          billingSelected: true,
                          openingTimes: false,
                          storeSelected: false,
                          featuredItems: false
                        })
                      }
                    >
                      Billing
                    </button>
                  ) : (
                    <button
                      className="view-business-tab-active"
                      onClick={() =>
                        this.setState({
                          businessSelected: false,
                          billingSelected: true,
                          openingTimes: false,
                          storeSelected: false,
                          featuredItems: false
                        })
                      }
                    >
                      Billing
                    </button>
                  )}
                  {this.state.storeSelected === false ? (
                    <button
                      onClick={() =>
                        this.setState({
                          businessSelected: false,
                          billingSelected: false,
                          openingTimes: false,
                          storeSelected: true,
                          featuredItems: false
                        })
                      }
                    >
                      Store
                    </button>
                  ) : (
                    <button
                      className="view-business-tab-active"
                      onClick={() =>
                        this.setState({
                          businessSelected: false,
                          billingSelected: false,
                          openingTimes: false,
                          storeSelected: true,
                          featuredItems: false
                        })
                      }
                    >
                      Store
                    </button>
                  )}
                  {this.state.featuredItems === false ? (
                    <button
                      onClick={() =>
                        this.setState({
                          businessSelected: false,
                          billingSelected: false,
                          openingTimes: false,
                          storeSelected: false,
                          featuredItems: true
                        })
                      }
                    >
                      Featured Items
                    </button>
                  ) : (
                    <button
                      className="view-business-tab-active"
                      onClick={() =>
                        this.setState({
                          businessSelected: false,
                          billingSelected: false,
                          openingTimes: false,
                          storeSelected: false,
                          featuredItems: true
                        })
                      }
                    >
                      Featured Items
                    </button>
                  )}
                  {this.state.openingTimes === false ? (
                    <button
                      onClick={() =>
                        this.setState({
                          businessSelected: false,
                          billingSelected: false,
                          openingTimes: true,
                          storeSelected: false,
                          featuredItems: false
                        })
                      }
                    >
                      Opening Times
                    </button>
                  ) : (
                    <button
                      className="view-business-tab-active"
                      onClick={() =>
                        this.setState({
                          businessSelected: false,
                          billingSelected: false,
                          openingTimes: true,
                          storeSelected: false,
                          featuredItems: false
                        })
                      }
                    >
                      Opening Times
                    </button>
                  )}
                </div>

                {this.state.businessSelected === true ? (
                  <div className="main-field-container">
                    <div className="field-container">
                      <span>Company name: </span>
                      <span className="email-span">{x.company_name}</span>
                    </div>
                    <div className="field-container">
                      <span>Branding: </span>
                      <span className="email-span">{x.branding} </span>
                    </div>
                    <div className="field-container">
                      <span>Type of business: </span>
                      <span className="email-span">{x.type_of_business} </span>
                    </div>
                    <div className="field-container">
                      <span>Business Category: </span>
                      <span className="email-span">{x.business_category} </span>
                    </div>
                    <div className="field-container">
                      <span>Business Email: </span>
                      <span className="email-span">{x.business_email} </span>
                    </div>
                    <div className="field-container">
                      <span>Business contact: </span>
                      <span className="email-span">{x.business_contact} </span>
                    </div>
                    <div className="field-container">
                      <span>Business Number: </span>
                      <span className="email-span">{x.business_number} </span>
                    </div>
                    <div className="field-container">
                      <span>Branch: </span>
                      <span className="email-span">{x.branch} </span>
                    </div>
                    <div className="field-container">
                      <span>Account Number: </span>
                      <span className="email-span">{x.account_number} </span>
                    </div>
                  </div>
                ) : null}
                {this.state.billingSelected === true ? (
                  <div className="main-field-container">
                    <div className="field-container">
                      <span>Tax number: </span>
                      <span className="email-span">{x.tax_number} </span>
                    </div>
                    <div className="field-container">
                      <span>Payment method: </span>
                      <span className="email-span">{x.payment_method} </span>
                    </div>
                    <div className="field-container">
                      <span>Bank: </span>
                      <span className="email-span">{x.bank} </span>
                    </div>
                    <div className="field-container">
                      <span>Currency: </span>
                      <span className="email-span">{x.currency} </span>
                    </div>
                    <div className="field-container">
                      <span>Billing address: </span>
                      <span className="email-span">{x.billing_address} </span>
                    </div>
                    <div className="field-container">
                      <span>Postcode: </span>
                      <span className="email-span">{x.postcode} </span>
                    </div>
                    <div className="field-container">
                      <span>State: </span>
                      <span className="email-span">{x.state} </span>
                    </div>
                    <div className="field-container">
                      <span>Country: </span>
                      <span className="email-span">{x.country} </span>
                    </div>
                  </div>
                ) : null}
                {this.state.storeSelected === true ? (
                  <div className="main-field-container">
                    <div className="field-container">
                      <span>Logo: </span>
                      <span className="email-span">Logo</span>
                    </div>
                    <div className="field-container">
                      <span>Name: </span>
                      <span className="email-span">James Padding </span>
                    </div>
                    <div className="field-container">
                      <span>No: </span>
                      <span className="email-span">123456 </span>
                    </div>
                    <div className="field-container">
                      <span>Description: </span>
                      <span className="email-span">Description</span>
                    </div>
                  </div>
                ) : null}

                {this.state.featuredItems === true ? (
                  <div className="main-field-container">
                    <div className="featured-item-field-container">
                      <div className="featured-item-img-container">
                        <img src="https://vignette.wikia.nocookie.net/insatiablenetflix/images/3/37/36160115_486814051774091_6550841096618901504_n.jpg/revision/latest?cb=20180811150848" />
                      </div>
                      <div className="featured-item-desc-container">
                        <span>Krusty Krab</span>
                        <p>Description</p>
                      </div>
                      <div className="featured-item-price-container">
                        <span>$10</span>
                      </div>
                    </div>
                    <div className="featured-item-field-container">
                      <div className="featured-item-img-container">
                        <img src="https://vignette.wikia.nocookie.net/insatiablenetflix/images/3/37/36160115_486814051774091_6550841096618901504_n.jpg/revision/latest?cb=20180811150848" />
                      </div>
                      <div className="featured-item-desc-container">
                        <span>Krusty Krab</span>
                        <p>Description</p>
                      </div>
                      <div className="featured-item-price-container">
                        <span>$10</span>
                      </div>
                    </div>
                    <div className="featured-item-field-container">
                      <div className="featured-item-img-container">
                        <img src="https://vignette.wikia.nocookie.net/insatiablenetflix/images/3/37/36160115_486814051774091_6550841096618901504_n.jpg/revision/latest?cb=20180811150848" />
                      </div>
                      <div className="featured-item-desc-container">
                        <span>Krusty Krab</span>
                        <p>Description</p>
                      </div>
                      <div className="featured-item-price-container">
                        <span>$10</span>
                      </div>
                    </div>
                  </div>
                ) : null}

                {this.state.openingTimes === true ? (
                  <div className="main-field-container">
                    <div className="field-container">
                      <span>Sun: </span>
                      <span className="email-span">Logo</span>
                    </div>
                    <div className="field-container">
                      <span>Sat: </span>
                      <span className="email-span">James Padding </span>
                    </div>
                    <div className="field-container">
                      <span>Fri: </span>
                      <span className="email-span">123456 </span>
                    </div>
                    <div className="field-container">
                      <span>Thurs: </span>
                      <span className="email-span">Description</span>
                    </div>
                    <div className="field-container">
                      <span>Wed: </span>
                      <span className="email-span">James Padding </span>
                    </div>
                    <div className="field-container">
                      <span>Tues: </span>
                      <span className="email-span">123456 </span>
                    </div>
                    <div className="field-container">
                      <span>Mon: </span>
                      <span className="email-span">Description</span>
                    </div>
                  </div>
                ) : null}
              </div>
            ))}
          </div>
        ) : null}
      </div>
    );
  }
}

export default index;
