import React, { Component } from "react";
import Table from "../../../components/Table";
import data from "../../../data/data.json";
import { url } from "../../../config";

export class index extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isChecked: false,
      filterUsers: "all",
      business: [],
      businesData: [],
      selectedData: [],
      rowSelected: false,
      businessSelected: true,
      billingSelected: false
    };
  }

  /**
  |--------------------------------------------------
  | Upon page loading, fetches all the data from the api
  |--------------------------------------------------
  */
  componentDidMount() {
    this.fetchAllBusiness();
  }

  /**
  |--------------------------------------------------
  | receives and sets state 
    selected row data from the Table component
  |--------------------------------------------------
  */
  passedFromChild = (i, state) => {
    let arr = this.state.selectedData;
    console.log("Passed:", this.state.businessData[i]);

    arr.push(this.state.businessData[i]);
    this.setState({ rowSelected: state });
    console.log("Details saved", this.state.selectedData);
  };

  /**
  |--------------------------------------------------
  | fetches data from api
  |--------------------------------------------------
  */
  fetchAllBusiness = () => {
    fetch(`${url}/api/business`, {
      method: "GET",
      mode: "cors",
      headers: {
        "Access-Control-Allow-Origin": "*",
        "Content-Type": "application/json; charset=utf-8",
        Accept: "application/json"
      }
    })
      .then(res => res.json())
      .then(data => {
        if (data.success) {
          let business = [];
          console.log("Business: ", data.businesses);
          data.businesses.map(x => {
            let merchant = {
              f_name: x.f_name,
              l_name: x.l_name,
              business_category: x.business_category,
              company_name: x.company_name
            };
            business.push(merchant);
          });
          console.log("Final Merchants: ", business);
          this.setState({ business, businessData: data.businesses });
        }
      })
      .catch(err => {
        console.log("Error for business page", err);

        alert(
          "Error connecting to server",

          [{ text: "OK", onClick: () => null }],
          { cancelable: false }
        );
      });
  };

  /**
  |--------------------------------------------------
  | changes state onToggle
  |--------------------------------------------------
  */
  toggle = async () => {
    await this.setState({ isChecked: !this.state.isChecked });
    console.log(this.state.isChecked);
    if (!this.state.isChecked) {
      console.log("Users Page");
    } else if (this.state.isChecked) {
      setTimeout(() => {
        this.props.history.push("/useragent");
      }, 400);
    }
  };

  render() {
    return (
      <div className="dashboard-container">
        {this.state.rowSelected === true ? (
          <div className="modal-opacity" />
        ) : null}
        <div className="dashboard-header" style={{ background: "red" }}>
          <h3 className="table-page-title">Business</h3>
          <div className="business-search-container">
            <div className="business-search-one">
              <input className="form-control" />
              <select class="browser-default custom-select business-select-1">
                <option>Merchant Name</option>
                <option value="1">Business Name</option>
              </select>
            </div>
            <div className="business-search-two">
              <p>Bye</p>
            </div>
          </div>
        </div>

        <div className="container-fluid" style={{ paddingTop: "13rem" }}>
          <Table
            head={data.tHeadMerchantBusiness}
            body={this.state.business}
            data={this.state.businessData}
            method={this.passedFromChild}
          />
        </div>

        {this.state.rowSelected === true ? (
          <div>
            {this.state.selectedData.map((x, i) => (
              <div className="view-business-container" key={i}>
                <button
                  className="exit-btn-view-business"
                  onClick={() => this.setState({ rowSelected: false })}
                >
                  <img
                    alt="selected-user-x-icon"
                    src="https://img.pngio.com/index-of-v2-imgs-x-png-black-and-white-256_256.png"
                  />
                </button>
                <div className="business-profile-container">
                  <img
                    alt="selected-user-profile-icon"
