import React, { Component } from "react";

export class index extends Component {
  constructor(props) {
    super(props);

    this.state = {
      w: "",
      bAccRow: false
    };
  }

  componentDidMount() {
    console.log("body: ", this.props.body);
  }

  render() {
    return (
      <div>
        <table className="table fixed-table-head table-bordered">
          <thead>
            <tr
              style={{
                background: "rgb(248,248,248)",
                color: "rgb(204,204,204)"
              }}
            >
              {this.props.head.map(x => (
                <th
                  key={x.id}
                  style={{ width: x.width, textAlign: "center", border: 0 }}
                >
                  {x.title}
                </th>
              ))}
            </tr>
          </thead>
        </table>
        <table
          className="table table-bordered user-table"
          style={{ fontSize: 14 }}
        >
          <tbody>
            {/* {this.props.body.map(x => (
              <tr
                key={x.id}
                style={{ width: x.width, textAlign: "center", border: 0 }}
              >
                {x.length.map(y => (
                  <td key={y}>{y}</td>
                ))}
                {x.title}
              </tr>
            ))} */}
            {this.props.body.map((x, i) => (
              <tr>
                {!x.f_name ? (
                  <td key={x.id} style={{ display: "none" }}>
                    {x.f_name}
                  </td>
                ) : (
                  <td key={x.id} style={{ width: this.props.head[i].width }}>
                    {x.f_name}
                  </td>
                )}

                {!x.l_name ? (
                  <td style={{ display: "none" }}>{x.l_name}</td>
                ) : (
                  <td style={{ width: "12.5%", textAlign: "center" }}>
                    {x.l_name}
                  </td>
                )}

                {!x.contact ? (
                  <td style={{ display: "none" }}>{x.contact}</td>
                ) : (
                  <td style={{ width: "12.5%", textAlign: "center" }}>
                    {x.contact}
                  </td>
                )}

                {!x.facebook_id ? (
                  <td style={{ display: "none" }}>{x.facebook_id}</td>
                ) : (
                  <td style={{ width: "12.5%", textAlign: "center" }}>
                    {x.facebook_id}
                  </td>
                )}

                {!x.google_id ? (
                  <td style={{ display: "none" }}>{x.google_id}</td>
                ) : (
                  <td style={{ width: "12.5%", textAlign: "center" }}>
                    {x.google_id}
                  </td>
                )}

                {!x.credits ? (
                  <td style={{ display: "none" }}>{x.credits}</td>
                ) : (
                  <td style={{ width: "12.5%", textAlign: "center" }}>
                    {x.credits}
                  </td>
                )}

                {!x.dateCreated ? (
                  <td style={{ display: "none" }}>{x.dateCreated}</td>
                ) : (
                  <td style={{ width: "12.5%", textAlign: "center" }}>
                    {x.dateCreated}
                  </td>
                )}

                {!x.merchant ? (
                  <td style={{ display: "none" }}>{x.merchant}</td>
                ) : (
                  <td>{x.merchant}</td>
                )}
                {!x.business_email ? (
                  <td style={{ display: "none" }}>{x.business_email}</td>
                ) : (
                  <td>{x.business_email}</td>
                )}
                {!x.business_contact ? (
                  <td style={{ display: "none" }}>{x.business_contact}</td>
                ) : (
                  <td>{x.business_contact}</td>
                )}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  }
}

export default index;
