import React, { Component } from "react";
import Table from "../../../components/Table";
import data from "../../../data/data.json";
import { url } from "../../../config";

export class index extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isChecked: false,
      filterUsers: "all",
      business: [],
      businesData: [],
      selectedData: [],
      typeOfBusiness: [],
      tempArr: [],
      rowSelected: false,
      businessSelected: true,
      billingSelected: false,
      searchValue: "",
      selectOption: "",
      searchBusiness: ""
    };
  }

  /**
  |--------------------------------------------------
  | Upon page loading, fetches all the data from the api
  |--------------------------------------------------
  */
  componentDidMount() {
    this.fetchAllBusiness();
    this.state.typeOfBusiness.map(x => this.setState({ searchBusiness: x }));
  }

  /**
  |--------------------------------------------------
  | receives and sets state 
    selected row data from the Table component
  |--------------------------------------------------
  */
  passedFromChild = (i, state) => {
    let arr = this.state.selectedData;
    console.log("Passed:", this.state.businessData[i]);

    arr.push(this.state.businessData[i]);
    this.setState({ rowSelected: state });
    console.log("Details saved", this.state.selectedData);
  };

  /**
  |--------------------------------------------------
  | fetches data from api
  |--------------------------------------------------
  */
  fetchAllBusiness = () => {
    fetch(`${url}/api/business`, {
      method: "GET",
      mode: "cors",
      headers: {
        "Access-Control-Allow-Origin": "*",
        "Content-Type": "application/json; charset=utf-8",
        Accept: "application/json"
      }
    })
      .then(res => res.json())
      .then(data => {
        if (data.success) {
          let business = [];
          console.log("Business: ", data.businesses);
          data.businesses.map(x => {
            this.state.typeOfBusiness.push(x.type_of_business);
            this.state.typeOfBusiness.map(x =>
              this.setState({ searchBusiness: x })
            );
            console.log("Businessess Type", this.state.searchBusiness);
            let merchant = {
