import React, { Component } from "react";
import Table from "../../../components/Table";
import data from "../../../data/data.json";
import { url } from "../../../config";

export default class index extends Component {
  constructor(props) {
    super(props);

    this.state = {
      name: "",
      desc: "",
      amount: "",
      quantity: "",
      expiry: "",
      addVoucher: false
    };
  }

  /**
  |--------------------------------------------------
  | navigates to view voucher page
  |--------------------------------------------------
  */
  viewVoucher = () => {
    this.props.history.push("/viewvoucher");
  };
  render() {
    return (
      <div className="main-dashboard-container">
        <h3 className="page-title">Vouchers</h3>
        <button type="button" className="btn btn-primary add-voucher-btn">
          <img
            src="https://png.pngtree.com/svg/20161118/6fc980719c.svg"
            alt="add-modal-icon"
          />
        </button>
        {/* Modal */}
        <div className="cat-popup-container">
          <div>
            <h3 className="cat-pop-title">Category Title</h3>
          </div>
          <div>
            <input
              className="form-control cat-pop-input"
              value={this.state.title}
              onChange={e => this.setState({ title: e.target.value })}
            />
            <button
              className="btn cat-save-btn"
              onClick={() => this.addCategory()}
            >
              Save
            </button>
          </div>
        </div>
        <div className="container-fluid">
          <Table
            head={data.tHeadFeedbackTable}
            body={this.state.feedback}
            method={this.passedFromChild}
          />
        </div>
      </div>
    );
  }
}
