import React, { Component } from "react";
import Table from "../../../components/Table";
import data from "../../../data/data.json";
import { url } from "../../../../config";

export class index extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isChecked: false,
      filterUsers: "all",
      users: []
    };
  }

  /**
  |--------------------------------------------------
  | on load page calls fetchAllUsers function
  |--------------------------------------------------
  */
  componentDidMount() {
    this.fetchAllUsers();
  }

  /**
  |--------------------------------------------------
  | fetches all data for users from api and stores
    in an array
  |--------------------------------------------------
  */
  fetchAllUsers = () => {
    fetch(`${url}/api/users`, {
      method: "GET",
      mode: "cors",
      headers: {
        "Access-Control-Allow-Origin": "*",
        "Content-Type": "application/json; charset=utf-8",
        Accept: "application/json"
      }
    })
      .then(res => res.json())
      .then(data => {
        if (data.success) {
          let users = [];
          data.users.map(x => {
            let user = {
              f_name: x.f_name,
              l_name: x.l_name,
              email: x.email,
              contact: x.contact,
              facebook_id: x.facebook_id,
              google_id: x.google_id,
              credits: x.credits,
              dateCreated: x.dateCreated
            };
            users.push(user);
          });

          console.log("Final Users:", users);
          this.setState({ users });
        }
      })
      .catch(err => {
        console.log("Error for users page", err);

        alert(
          "Error connecting to server",

          [{ text: "OK", onClick: () => null }],
          { cancelable: false }
        );
      });
  };

  /**
  |--------------------------------------------------
  | changes state on click toggle button
  |--------------------------------------------------
  */
  toggle = async () => {
    await this.setState({ isChecked: !this.state.isChecked });
    console.log(this.state.isChecked);
    if (!this.state.isChecked) {
      console.log("Users Page");
    } else if (this.state.isChecked) {
      setTimeout(() => {
        this.props.history.push("/useragent");
      }, 400);
    }
  };

  /**
  |--------------------------------------------------
  | renders users page
  |--------------------------------------------------
  */
  render() {
    return (
      <div className="dashboard-container">
        <div className="dashboard-header">
          <h3 className="table-page-title">Users</h3>
          <div className="filter-users-container">
            <div className="user-toggle">
              <span className="user-toggle-text">Users</span>
              <div className="switch-container">
                <label>
                  <input ref="switch" className="switch" type="checkbox" />
                  <div>
                    <div />
                  </div>
                </label>
              </div>
              <span>Agents</span>
            </div>
            <select
              className="form-control filter-pages"
              value={this.state.filterUsers}
              onChange={this.filterPage}
            >
              <option value="all">All</option>
              <option value="email">Email</option>
              <option value="gmail">Gmail</option>
              <option value="fb">Facebook</option>
            </select>
          </div>
        </div>

        <div className="container-fluid" style={{ paddingTop: "14.5rem" }}>
          <Table head={data.tHeadUsers} body={this.state.users} />
        </div>
      </div>
    );
  }
}

export default index;
