            <div className="main-field-container">
              <div className="field-container">
                <span>Tax number: </span>
                <span className="email-span">arianabaros@gmail.com </span>
              </div>
              <div className="field-container">
                <span>Payment method: </span>
                <span className="email-span">arianabaros@gmail.com </span>
              </div>
              <div className="field-container">
                <span>Bank: </span>
                <span className="email-span">arianabaros@gmail.com </span>
              </div>
              <div className="field-container">
                <span>Currency: </span>
                <span className="email-span">arianabaros@gmail.com </span>
              </div>
              <div className="field-container">
                <span>Billing address: </span>
                <span className="email-span">arianabaros@gmail.com </span>
              </div>
              <div className="field-container">
                <span>Postcode: </span>
                <span className="email-span">arianabaros@gmail.com </span>
              </div>
              <div className="field-container">
                <span>State: </span>
                <span className="email-span">arianabaros@gmail.com </span>
              </div>
              <div className="field-container">
                <span>Country: </span>
                <span className="email-span">arianabaros@gmail.com </span>
