import React, { Component } from "react";
import Table from "../../../components/Table";
import data from "../../../data/data.json";
import { url } from "../../../config";

export default class index extends Component {
  constructor(props) {
    super(props);

    this.state = {
      pendingTab: true,
      approvedTab: false,
      declineTab: false,
      approveDecline: false,
      approved: [],
      pending: [],
      decline: [],
      business: []
    };
  }

  componentDidMount() {
    this.fetchBusiness();
  }

  fetchBusiness = () => {
    fetch(`${url}/api/business`, {
      method: "GET",
      mode: "cors",
      headers: {
        "Access-Control-Allow-Origin": "*",
        "Content-Type": "application/json; charset=utf-8",
        Accept: "application/json"
      }
    })
      .then(res => res.json())
      .then(data => {
        if (data.success) {
          console.log(data);
          // let business = [];
          // console.log("Business: ", data.businesses);
          // data.businesses.map(x => {
          //   let merchant = {
          //     f_name: x.f_name,
          //     l_name: x.l_name,
          //     business_category: x.business_category,
          //     company_name: x.company_name
          //   };
          //   business.push(merchant);
          // });
          // console.log("Final Merchants: ", business);
          // this.setState({ business, businessData: data.businesses });
        }
      })
      .catch(err => {
        console.log("Error for business page", err);

        alert(
          "Error connecting to server",

          [{ text: "OK", onClick: () => null }],
          { cancelable: false }
        );
      });
  };

  filterBusiness = () => {};

  /**
  |--------------------------------------------------
  | receives and sets state 
    selected row data from the Table component
  |--------------------------------------------------
  */
  passedFromChild = (i, state) => {
    console.log(state);

    // this.setState({ approveDecline: state });
  };

  render() {
    return (
      <div className="dashboard-container">
        {this.state.approveDecline === true ? (
          <div className="modal-opacity" />
        ) : null}
        <h3
          className="page-title business-request-title"
          style={{ marginLeft: "1rem" }}
        >
          Business Requests
        </h3>
        <div className="business-request-tabs-container">
          <button
            onClick={() =>
              this.setState({
                pendingTab: true,
                approvedTab: false,
                declineTab: false
              })
            }
          >
            Pending
          </button>
          <button
            onClick={() =>
              this.setState({
                pendingTab: false,
                approvedTab: true,
                declineTab: false
              })
            }
          >
            Approved
          </button>
          <button
            onClick={() =>
              this.setState({
                pendingTab: false,
                approvedTab: false,
                declineTab: true
              })
            }
          >
            Decline
          </button>
        </div>
        <div
          className="container-fluid bR-mobile-container"
          style={{ paddingTop: "5.2rem" }}
        >
          {this.state.pendingTab === true ? (
            <Table
              head={data.tHeadPendingBusinessTable}
              body={data.tBodyPendingBusinessTable}
              button={data.tBodyButton}
              method={this.passedFromChild}
            />
          ) : null}
          {this.state.approvedTab === true ? (
            <Table
              head={data.tHeadApprovedBusinessTable}
              body={data.tBodyApprovedBusinessTable}
              method={this.passedFromChild}
            />
          ) : null}
          {this.state.declineTab === true ? (
            <Table
              head={data.tHeadDeclineBusinessTable}
              body={data.tBodyDeclineBusinessTable}
              method={this.passedFromChild}
            />
          ) : null}
          {this.state.approveDecline === true ? (
            <div className="approve-decline-container">
              <button>Approve</button>
              <button onClick={() => this.setState({ approveDecline: false })}>
                Decline
              </button>
            </div>
          ) : null}
        </div>
      </div>
    );
  }
}
