import React, { Component } from "react";
import {
  VictoryPie,
  VictoryChart,
  VictoryLine,
  VictoryTheme,
  VictoryBar
} from "victory";

import Table from "../../../components/Table";
import data from "../../../data/data.json";
import { url } from "../../../config";

/**
|--------------------------------------------------
| renders the Dashboard page
|--------------------------------------------------
*/
export default class index extends Component {
  constructor(props) {
    super(props);

    this.state = {
      business: [],
      businesData: []
    };
  }

  componentDidMount() {
    this.fetchAllBusiness();
  }

  fetchAllBusiness = () => {
    fetch(`${url}/api/business`, {
      method: "GET",
      mode: "cors",
      headers: {
        "Access-Control-Allow-Origin": "*",
        "Content-Type": "application/json; charset=utf-8",
        Accept: "application/json"
      }
    })
      .then(res => res.json())
      .then(data => {
        if (data.success) {
          console.log("data", data);
          let business = [];

          data.businesses.map(x => {
            let merchant = {
              business_category: x.type_of_business,
              company_name: x.company_name
            };
            business.push(merchant);
          });

          this.setState({ business, businessData: data.businesses });
          console.log(this.state.business);
        }
      })
      .catch(err => {
        console.log("Error for business page", err);

        alert(
          "Error connecting to server",

          [{ text: "OK", onClick: () => null }],
          { cancelable: false }
        );
      });
  };

  render() {
    return (
      <div className="main-dashboard-container">
        <h3 className="page-title">Dashboard</h3>
        <div className="container-fluid">
          <div className="graph">
            <div className="graph-container">
              <p>Graph</p>
              {/* <VictoryChart theme={VictoryTheme.material}>
                <VictoryLine
                  style={{
                    data: { stroke: "rgb(12,96,250)" },
                    parent: { border: "1px solid #ccc" }
                  }}
                  data={[
                    { x: 1, y: 2 },
                    { x: 2, y: 3 },
                    { x: 3, y: 5 },
                    { x: 4, y: 4 },
                    { x: 5, y: 7 }
                  ]}
                  animate={{
                    duration: 2000,
                    onLoad: { duration: 2000 }
                  }}
                />
              </VictoryChart> */}
              <VictoryChart
                theme={VictoryTheme.material}
                domainPadding={{ x: 10 }}
              >
                <VictoryBar
                  horizontal
                  cornerRadius={10}
                  data={this.state.business}
                  style={{
                    data: {
                      fill: ({ y }) =>
                        y > 18 ? "rgb(95,153,252)" : "rgb(12,96,250)"
                    }
                  }}
                  animate={{
                    duration: 2000,
                    onLoad: { duration: 2000 }
                  }}
                />
              </VictoryChart>
            </div>
            <div className="graph-container">
              <p>Graph</p>
              <VictoryPie
                innerRadius={200}
                data={[{ x: 11, y: 35 }, { x: 11, y: 40 }]}
                style={{
                  data: {
                    fill: ({ y }) =>
                      y > 39 ? "rgb(95,153,252)" : "rgb(12,96,250)"
                  }
                }}
                animate={{
                  duration: 2000,
                  onLoad: { duration: 2000 }
                }}
              />
            </div>
            <div className="graph-container">
              <p>Graph</p>
              <VictoryChart theme={VictoryTheme.material}>
                <VictoryLine
                  style={{
                    data: { stroke: "rgb(12,96,250)" },
                    parent: { border: "1px solid #ccc" }
                  }}
                  data={[
                    { x: 1, y: 2 },
                    { x: 2, y: 3 },
                    { x: 3, y: 5 },
                    { x: 4, y: 4 },
                    { x: 5, y: 7 }
                  ]}
                  animate={{
                    duration: 2000,
                    onLoad: { duration: 2000 }
                  }}
                />
              </VictoryChart>
            </div>
            <div className="graph-container">
              <p>Graph</p>
              <VictoryPie
                innerRadius={200}
                data={[{ x: 11, y: 35 }, { x: 11, y: 40 }]}
                style={{
                  data: {
                    fill: ({ y }) =>
                      y > 39 ? "rgb(95,153,252)" : "rgb(12,96,250)"
                  }
                }}
                animate={{
                  duration: 2000,
                  onLoad: { duration: 2000 }
                }}
              />
            </div>
          </div>
          <div className="graph">
            <div className="graph-container-2">
              <p>Graph/Report </p>
              <VictoryChart
                theme={VictoryTheme.material}
                domainPadding={{ x: 10 }}
              >
                <VictoryBar
                  cornerRadius={10}
                  data={[
                    { x: 11, y: 35 },
                    { x: 12, y: 40 },
                    { x: 13, y: 55 },
                    { x: 14, y: 85 },
                    { x: 15, y: 95 },
                    { x: 16, y: 105 }
                  ]}
                  style={{
                    data: {
                      fill: ({ y }) =>
                        y > 39 ? "rgb(95,153,252)" : "rgb(12,96,250)"
                    }
                  }}
                  animate={{
                    duration: 2000,
                    onLoad: { duration: 2000 }
                  }}
                />
              </VictoryChart>
            </div>
            <div className="graph-container-2">
              <p>Graph/Report</p>
              <VictoryChart
                theme={VictoryTheme.material}
                domainPadding={{ x: 10 }}
              >
                <VictoryBar
                  horizontal
                  cornerRadius={10}
                  data={[
                    { x: 11, y: 35 },
                    { x: 12, y: 40 },
                    { x: 13, y: 55 },
                    { x: 14, y: 85 },
                    { x: 15, y: 95 },
                    { x: 16, y: 105 }
                  ]}
                  style={{
                    data: {
                      fill: ({ y }) =>
                        y > 39 ? "rgb(95,153,252)" : "rgb(12,96,250)"
                    }
                  }}
                  animate={{
                    duration: 2000,
                    onLoad: { duration: 2000 }
                  }}
                />
              </VictoryChart>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
