import React, { Component } from "react";
import { VictoryPie, VictoryChart, VictoryTheme, VictoryBar } from "victory";
import { url } from "../../../config";

/**
|--------------------------------------------------
| renders the Dashboard page
|--------------------------------------------------
*/
export default class index extends Component {
  constructor(props) {
    super(props);

    this.state = {
      business: [],
      businesData: [],
      stats: [],
      users: [],
      feedback: [],
      pending: [],
      pendingbusiness: []
    };
  }

  componentDidMount() {
    this.fetchAllBusiness();
    this.fetchAllUsers();
    this.fetchAllFeedback();
    this.fetchAgents();
    this.fetchBusinessRequest();
  }

  fetchAgents = async () => {
    fetch(`${url}/api/agents`, {
      method: "GET",
      mode: "cors",
      headers: {
        "Access-Control-Allow-Origin": "*",
        "Content-Type": "application/json; charset=utf-8",
        Accept: "application/json"
      }
    })
      .then(res => res.json())
      .then(data => {
        if (data.success) {
          // console.log("User Agent Stuff:");
          // console.log(data.agent);
          if (data.agent.length >= 1) {
            let business = data.agent;
            let pending = [];
            business.map(x =>
              x.isPending ? pending.push(x) : console.log("No Status")
            );
            console.log(pending);

            this.setState({ pending });
          }
        }
      })
      .catch(err => {
        console.log("Error for dashboard page", err);

        // alert(
        //   "Error connecting to server",

        //   [{ text: "OK", onClick: () => null }],
        //   { cancelable: false }
        // );
      });
  };

  fetchBusinessRequest = async () => {
    fetch(`${url}/api/business`, {
      method: "GET",
      mode: "cors",
      headers: {
        "Access-Control-Allow-Origin": "*",
        "Content-Type": "application/json; charset=utf-8",
        Accept: "application/json"
      }
    })
      .then(res => res.json())
      .then(data => {
        if (data.success) {
          if (data.agent.length >= 1) {
            let business = data.agent;
            let pendingbusiness = [];
            business.map(x =>
              x.isPending ? pendingbusiness.push(x) : console.log("No Status")
            );
            console.log(pendingbusiness);

            this.setState({ pendingbusiness });
          }
        }
      })
      .catch(err => {
        console.log("Error for dashboard page", err);

        alert(
          "Error connecting to server",

          [{ text: "OK", onClick: () => null }],
          { cancelable: false }
        );
      });
  };

  fetchAllFeedback = () => {
    fetch(`${url}/api/feedbacks`, {
      method: "GET",
      mode: "cors",
      headers: {
        "Access-Control-Allow-Origin": "*",
        "Content-Type": "application/json; charset=utf-8",
        Accept: "application/json"
      }
    })
      .then(res => res.json())
      .then(data => {
        if (data.success) {
          if (data.feedbacks.length >= 1) {
            let feedback = [];
            data.feedbacks.map(x => {
              let user = {
                _id: x._id,
                rating: x.rating,
                description: x.description,
                dateCreated: x.dateCreated
              };
              feedback.push(user);
            });

            this.setState({ feedback });
            console.log(this.state.feedback);
          }
        }
      })
      .catch(err => {
        console.log("Error for users page", err);

        alert(
          "Error connecting to server",

          [{ text: "OK", onClick: () => null }],
          { cancelable: false }
        );
      });
  };

  fetchAllBusiness = () => {
    fetch(`${url}/api/business`, {
      method: "GET",
      mode: "cors",
      headers: {
        "Access-Control-Allow-Origin": "*",
        "Content-Type": "application/json; charset=utf-8",
        Accept: "application/json"
      }
    })
      .then(res => res.json())
      .then(data => {
        if (data.success) {
          if (data.businesses.length >= 1) {
            let business = [];
            let stats = [];

            data.businesses.map(x => {
              let statistics = {
                x: x.type_of_business,
                y: x.company_name
              };
              stats.push(statistics);
              this.setState({ stats });
            });

            this.setState({ business, businessData: data.businesses });
            // console.log(this.state.business);
          }
        }
      })
      .catch(err => {
        console.log("Error for business page", err);

        alert(
          "Error connecting to server",

          [{ text: "OK", onClick: () => null }],
          { cancelable: false }
        );
      });
  };

  fetchAllUsers = () => {
    fetch(`${url}/api/users`, {
      method: "GET",
      mode: "cors",
      headers: {
        "Access-Control-Allow-Origin": "*",
        "Content-Type": "application/json; charset=utf-8",
        Accept: "application/json"
      }
    })
      .then(res => res.json())
      .then(data => {
        if (data.success) {
          if (data.users.length >= 1) {
            let users = [];
            data.users.map(x => {
              let user = {
                x: x.user_type,
                y: data.users.length
              };
              users.push(user);
            });

            this.setState({ users });
          }
        }
      })
      .catch(err => {
        console.log("Error for users page", err);

        alert(
          "Error connecting to server",

          [{ text: "OK", onClick: () => null }],
          { cancelable: false }
        );
      });
  };

  render() {
    return (
      <div className="main-dashboard-container">
        <h3 className="page-title">Dashboard</h3>
        <div className="container-fluid">
          <div className="graph">
            <div className="graph-container">
              <p>Business Types</p>
              <VictoryChart
                theme={VictoryTheme.material}
                width={700}
                height={300}
              >
                <VictoryBar
                  horizontal
                  cornerRadius={10}
                  data={this.state.stats}
                  style={{
                    label: {
                      fontSize: 16
                    },
                    data: {
                      fill: ({ y }) =>
                        y > 18 ? "rgb(95,153,252)" : "rgb(12,96,250)"
                    }
                  }}
                  animate={{
                    duration: 2000,
                    onLoad: { duration: 2000 }
                  }}
                />
              </VictoryChart>
            </div>
            <div className="graph-container">
              <p>User Vs User Agents</p>
              {/* <VictoryChart
                theme={VictoryTheme.material}
                width={700}
                height={300}
              >
                <VictoryBar
                  cornerRadius={10}
                  data={this.state.users}
                  style={{
                    label: {
                      fontSize: 16
                    },
                    data: {
                      fill: ({ y }) =>
